class SoilMoistureARViewController: UIViewController, ARSCNViewDelegate {

    // Declare variables
    var sceneView: ARSCNView!
    var locationManager: CLLocationManager!
    var moistureLevelNode: SCNNode!
    var soilMoistureData: Double = 0.0

    override func viewDidLoad() {
        super.viewDidLoad()

        // Set up AR scene
        sceneView = ARSCNView(frame: view.frame)
        sceneView.delegate = self
        view.addSubview(sceneView)

        // Set up location manager
        locationManager = CLLocationManager()
        locationManager.requestWhenInUseAuthorization()

        // Load moisture level node
        let moistureLevelScene = SCNScene(named: "moisture_level.scn")
        moistureLevelNode = moistureLevelScene?.rootNode.childNode(withName: "moisture_level", recursively: false)!
        moistureLevelNode.position = SCNVector3(x: 0, y: 0, z: -0.2)
        sceneView.scene.rootNode.addChildNode(moistureLevelNode)

        // Start AR session
        let configuration = ARWorldTrackingConfiguration()
        sceneView.session.run(configuration)
    }

    // Function to update soil moisture data
    func updateSoilMoistureData() {
        // Get soil moisture data from sensors
        soilMoistureData = getSoilMoistureData()

        // Update moisture level node
        let moistureLevel = soilMoistureData * 100.0
        let moistureLevelText = String(format: "%.2f", moistureLevel)
        let moistureLevelTextNode = moistureLevelNode.childNode(withName: "moisture_level_text", recursively: false)!
        moistureLevelTextNode.geometry?.firstMaterial?.diffuse.contents = moistureLevelText
    }

    // Function to get soil moisture data from sensors
