import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(
        'http://example.com/api/data',
      );
      setData(result.data);
    };
    fetchData();
  }, []);

  return (
    <div>
      <h1>Farm Management Dashboard</h1>
      <table>
        <thead>
          <tr>
            <th>Field Name</th>
            <th>Crop</th>
            <th>Soil Moisture</th>
            <th>Temperature</th>
            <th>Humidity</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {data.map(item => (
            <tr key={item.id}>
              <td>{item.field_name}</td>
              <td>{item.crop}</td>
              <td>{item.soil_moisture}</td>
              <td>{item.temperature}</td>
              <td>{item.humidity}</td>
              <td>{item.status}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
```
