% Minimize the distance between sample and population moments using GMM
options = optimoptions('fminunc','Display','iter','Algorithm','quasi-newton','MaxFunctionEvaluations',10000);
[theta_hat,~,~] = fminunc(@(theta) moments(theta)*W*moments(theta)', theta0, options);

% Estimated parameter values
dscale_hat = theta_hat(1);
dshape_hat = theta_hat(2);
fc_hat = theta_hat(3);
cc_hat = theta_hat(4);

% Use estimated parameters to compute stockP and stockT
% Initialize vectors to store computed values
stockP = zeros(size(y));
stockT = zeros(size(y));

% Loop over the values in y
for i = 1:length(y)
    % Compute stockP using estimated parameters
    lambdap = zeros(size(y));
    t2 = cc_hat + fc_hat;
    for j = 1:length(y)
        t1 = y(j) - r*U - r*cc_hat;
        lambdap(j) = t1/t2;
    end

    jobsP = G(lambdap, dscale_hat, dshape_hat) - G(1e-8, dscale_hat, dshape_hat);
    stockP(i) = integral(@(lambda) (1./lambda) .* jobsP, 1e-8, lambdap(i));

    % Compute stockT using estimated parameters
