use Aws\Sns\SnsClient;
use Aws\Sns\Exception\SnsException;
use Aws\PinpointEmail\PinpointEmailClient;
use Aws\PinpointEmail\Exception\PinpointEmailException;

function handleIncomingSms($event)
{
    $message = $event['Records'][0]['Sns']['Message'];
    $messageId = $event['Records'][0]['Sns']['MessageId'];
    $sender = $event['Records'][0]['Sns']['MessageAttributes']['AWS.SNS.SMS.SMSSender']['Value'];

    // Process the message and generate a response
    $responseBody = "Thanks for your message! Your message ID is " . $messageId;

    // Send the response back via SMS
    $sns = new SnsClient([
        'version' => 'latest',
        'region' => 'us-east-1',
    ]);

    $params = [
        'Message' => $responseBody,
        'PhoneNumber' => $sender,
        'MessageAttributes' => [
            'AWS.SNS.SMS.SMSType' => [
                'DataType' => 'String',
                'StringValue' => 'Transactional'
            ],
            'AWS.SNS.SMS.SMSOriginationNumber' => [
                'DataType' => 'String',
                'StringValue' => '+1234567890' // replace with your SMS origination number
            ],
            'AWS.SNS.SMS.SMSMessageId' => [
                'DataType' => 'String',
                'StringValue' => $messageId
            ]
        ]
    ];

    try {
        $result = $sns->publish($params);
        return response()->json(['status' => 'success']);
    } catch (SnsException $e) {
        return response()->json(['status' => 'error', 'message' => $e->getMessage()]);
    }
}

function handleIncomingEmail($event)
{
    $messageId = $event['Records'][0]['ses']['mail']['messageId'];
    $bucketName = 'your-bucket-name';

    // Save the incoming email to an S3 bucket
    $s3 = new S3Client([
        'version' => 'latest',
        'region' => 'us-east-1',
    ]);

    $objectKey = 'incoming-emails/' . $messageId;
    $s3->putObject([
        'Bucket' => $bucketName,
        'Key' => $objectKey,
        'Body' => json_encode($event),
    ]);

