use serde::{Deserialize, Serialize};
use eventstore::{EventData, EventStore, EventStoreError, PersistentSubscriptionSettings};

#[derive(Serialize, Deserialize)]
struct AccountEvent {
    event_type: String,
    amount: i32,
}

#[derive(Serialize, Deserialize)]
struct Account {
    balance: i32,
}

impl Account {
    fn new() -> Account {
        Account { balance: 0 }
    }

    fn deposit(&mut self, amount: i32) {
        self.balance += amount;
    }

    fn withdraw(&mut self, amount: i32) {
        self.balance -= amount;
    }

    fn balance(&self) -> i32 {
        self.balance
    }

    fn handle_event(&mut self, event: AccountEvent) {
        match event.event_type.as_ref() {
            "deposit" => self.deposit(event.amount),
            "withdraw" => self.withdraw(event.amount),
            _ => println!("Unknown event type"),
        }
    }
}

async fn main() -> Result<(), EventStoreError> {
    let eventstore = EventStore::builder()
        .single_node_connection("http://localhost:2113")
        .await?;

    let stream_name = "my-account";
    let account = Account::new();
    let mut account = account;

    eventstore
        .append_to_stream(
            &stream_name,
            vec![EventData::json(
                AccountEvent {
                    event_type: "deposit".to_string(),
                    amount: 100,
                },
            )],
        )
        .await?;

    eventstore
        .append_to_stream(
            &stream_name,
            vec![EventData::json(
                AccountEvent {
                    event_type: "withdraw".to_string(),
                    amount: 50,
                },
            )],
        )
        .await?;

    println!("Balance: {}", account.balance());

    let settings = PersistentSubscriptionSettings::default();
    let mut subscription = eventstore
        .subscribe_to_stream(&stream_name, &settings, move |event| {
            let event_data: AccountEvent = serde_json::from_slice(&event.data).unwrap();
            account.handle_event(event_data);
        })
        .await?;

    subscription.start().await?;

    Ok(())
}
```
